replace into creature_text_locale set creatureid=37221,groupid=1,id=0,locale='zhTW',text='那是什麼!前面那個!這可能嗎?英雄們，到我旁邊來!';
replace into creature_text_locale set creatureid=37221,groupid=0,id=0,locale='zhTW',text='這個地方的冰冷……我感到血液在凝結。';
